mapper.update:
update t_stock_date
set stock_date = #{stockDate,jdbcType=VARCHAR},
  is_trade_date = #{isTradeDate,jdbcType=VARCHAR},
  is_start_week_day = #{isStartWeekDay,jdbcType=VARCHAR},
  is_end_week_day = #{isEndWeekDay,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select 

from t_stock_date t
where t.stock_date <= #{stockDate,jdbcType=VARCHAR} and t.is_trade_date='00' order by t.stock_date desc limit #{limitSize,jdbcType=INTEGER}
